var SpotifyWebApi = require('./spotifyworker.js');
var spotify = new SpotifyWebApi(false);

spotify.getLatestAlbum('glenn gould', function(err, result) {
	if (err) {
		console.log("error");
		console.log(err);
	} else {
		for (var i = 0; i < result.length; i++) {
			console.log((i + 1) + " " + result[i]['artists'][0]['name'] + " " + result[i]['name'] + " " + result[i]['release_date']);
		}
	}
});